");' > build/version.js && rollup -f umd -g d3-collection:d3_collection,d3-dispatch:d3_dispatch,d3-dsv:d3_dsv -n d3_request -o build/d3-request.js -- index.js && echo "'use strict';

var XMLHttpRequest = require('xmlhttprequest').XMLHttpRequest;" > build/d3-request.node.js && rollup -f cjs -g d3-collection:d3_collection,d3-dispatch:d3_dispatch,d3-dsv:d3_dsv -- index.js | tail -n+3 >> build/d3-request.node.js
